var assert 	= require('assert');
var array	= require('../array');

describe('Unit Tests', function() {
	var likeArr = {
		'0':1,
		'1':2,
		length:2
	}
	it('like', function() {
		assert.ok(array.like(likeArr));
	});

	it('each', function() {
		array.each(likeArr,function(i,n){
			if(i==1){
				assert.equal(n,2)
			}
		});
	});

	it('map', function() {
		var arr = array.map(likeArr,function(item){
			return item+1;
		});

		assert.equal(arr[0],2)
	});

	it('filter', function() {
		var arr = array.filter(likeArr,function(item){
			if(item === 1){
				return true;
			}
		});

		assert.equal(arr[0],1);
		assert.equal(arr.length,1);
	});

	it('from', function() {
		var arr = array.from(likeArr);

		assert.equal(arr[0],1);
		assert.equal(arr[1],2);
		assert.equal(arr.length,2);
	});

	it('remove', function() {
		var arr = [1,2,3,4,5];
		var arr = array.remove(arr,[0,1,2]);

		assert.equal(arr[0],4);
		assert.equal(arr[1],5);
	});
});